// Extend the TCPDF class to create custom Header and Footer
$draft = site_url("images/watermark.png");
class MYPDF extends TCPDF {
    //Page header
    public function Header() {
        // get the current page break margin
        $bMargin = $this->getBreakMargin();
        // get current auto-page-break mode
        $auto_page_break = $this->AutoPageBreak;
        // disable auto-page-break
        $this->SetAutoPageBreak(false, 0);
        // set bacground image
        $img_file = site_url("images/watermark.png");
        $this->Image($img_file, 0, 0, 210, 297, '', '', '', false, 300, '', false, false, 0);
        // restore auto-page-break status
        $this->SetAutoPageBreak($auto_page_break, $bMargin);
        // set the starting point for the page content
        $this->setPageMark();

      //  $image_file = site_url("images/header.jpg");
      // $this->Image($image_file, 0, 0, 230, 30, 'JPG', '', 'T', false, 300, '', false, false, 0, false, false, false);
       // Set font
       $this->SetFont('helvetica', 'B', 20);
       // Title
       $this->Cell(0, 15, '', 0, false, 'C', 0, '', 0, false, 'M', 'M');
    }
    public function Footer() {
      $image_file =site_url("images/bottomborder.png");
      $this->Image($image_file, 0, 283, 210, "", "PNG", "", "T", false, 300, "", false, false, 0, false, false, false);
      // Position at 15 mm from bottom
      $this->SetY(-40);
      // Set font
      $this->SetFont("dejavusans", "", 9);
      // Page number
      // Logo
  }
}

